Fn::Sub:
{
  "Comment": "State machine that executes a FHIR bulk export job",
  "StartAt": "parallelHelper",
  "States": {
    "catchAllUpdateStatusToFailed": {
      "Type": "Task",
      "Resource": "${aa4695978c272b5ced15939e12d5d141}",
      "Parameters": {
        "globalParams.$": "$",
        "status": "failed"
      },
      "Retry": [
        {
          "ErrorEquals": [
            "States.ALL"
          ]
        }
      ],
      "End": true
    },
    "parallelHelper": {
      "Type": "Parallel",
      "End": true,
      "Catch": [
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "Next": "catchAllUpdateStatusToFailed",
          "ResultPath": "$.error"
        }
      ],
      "Branches": [
        {
          "StartAt": "startExportJob",
          "States": {
            "updateStatusToFailed": {
              "Type": "Task",
              "Resource": "${aa4695978c272b5ced15939e12d5d141}",
              "Parameters": {
                "globalParams.$": "$",
                "status": "failed"
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.ALL"
                  ]
                }
              ],
              "End": true
            },
            "updateStatusToCanceled": {
              "Type": "Task",
              "Resource": "${aa4695978c272b5ced15939e12d5d141}",
              "Parameters": {
                "globalParams.$": "$",
                "status": "canceled"
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.ALL"
                  ]
                }
              ],
              "End": true
            },
            "updateStatusToCompleted": {
              "Type": "Task",
              "Resource": "${aa4695978c272b5ced15939e12d5d141}",
              "Parameters": {
                "globalParams.$": "$",
                "status": "completed"
              },
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.ALL"
                  ]
                }
              ],
              "End": true
            },
            "startExportJob": {
              "Type": "Task",
              "Resource": "${bc92a824e91efbf644fa01e48d0c0e2e}",
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.ALL"
                  ]
                }
              ],
              "Next": "waitForExportJob"
            },
            "waitForExportJob": {
              "Type": "Wait",
              "Seconds": 10,
              "Next": "getJobStatus"
            },
            "getJobStatus": {
              "Type": "Task",
              "Resource": "${3de42af21211f8e13478993084efd06b}",
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.ALL"
                  ]
                }
              ],
              "Next": "choiceOnJobStatus"
            },
            "choiceOnJobStatus": {
              "Type": "Choice",
              "Choices": [
                {
                  "Variable": "$.executionParameters.isCanceled",
                  "BooleanEquals": true,
                  "Next": "stopExportJob"
                },
                {
                  "Variable": "$.executionParameters.glueJobRunStatus",
                  "StringEquals": "SUCCEEDED",
                  "Next": "updateStatusToCompleted"
                },
                {
                  "Or": [
                    {
                      "Variable": "$.executionParameters.glueJobRunStatus",
                      "StringEquals": "STARTING"
                    },
                    {
                      "Variable": "$.executionParameters.glueJobRunStatus",
                      "StringEquals": "RUNNING"
                    }
                  ],
                  "Next": "waitForExportJob"
                },
                {
                  "Or": [
                    {
                      "Variable": "$.executionParameters.glueJobRunStatus",
                      "StringEquals": "FAILED"
                    },
                    {
                      "Variable": "$.executionParameters.glueJobRunStatus",
                      "StringEquals": "TIMEOUT"
                    },
                    {
                      "Variable": "$.executionParameters.glueJobRunStatus",
                      "StringEquals": "STOPPING"
                    },
                    {
                      "Variable": "$.executionParameters.glueJobRunStatus",
                      "StringEquals": "STOPPED"
                    }
                  ],
                  "Next": "updateStatusToFailed"
                }
              ]
            },
            "stopExportJob": {
              "Type": "Task",
              "Resource": "${1f557922bd8ea1055de8a761d3221026}",
              "Retry": [
                {
                  "ErrorEquals": [
                    "States.ALL"
                  ]
                }
              ],
              "Next": "updateStatusToCanceled"
            }
          }
        }
      ]
    }
  }
}
